
public class A2Q2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double dist = 165;
		System.out.println("Enter the distance in km = "+dist);
		double m = dist*1000;
		double ft = dist*3280.8399;
		double in = dist*39370.0787;
		double cm = dist *100000;
		System.out.println(dist+" km is "+m+" metres");
		System.out.println(dist+" km is "+ft+" feet");
		System.out.println(dist+" km is "+in+" inch");
		System.out.println(dist+" km is "+cm+" centimetres");
		}

}
