
public class A2Q5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double rad = 7.0;
		System.out.println("Enter the radius of the hemisphere: "+rad);
		System.out.println("The surface area of the hemisphere is "+(float)(3*((Math.PI)*rad*rad)));
		System.out.println("The volume area of the hemisphere is "+(float)(2.0/3*((Math.PI)*rad*rad*rad)));
	}

}
